// src/App.js
import React, { useEffect, useState } from "react";
import "./index.css";
import {
  uploadExpenses,
  fetchExpenses,
  fetchMonthlySummary,
  fetchCategorySummary,
} from "./api";
import UploadForm from "./components/UploadForm";
import ExpenseTable from "./components/ExpenseTable";
import MonthlyBarChart from "./components/MonthlyBarChart";
import CategoryPieChart from "./components/CategoryPieChart";
import FilterBar from "./components/FilterBar";
import SummaryCards from "./components/SummaryCards";

function App() {
  const [expenses, setExpenses] = useState([]);
  const [monthlySummary, setMonthlySummary] = useState([]);
  const [categorySummary, setCategorySummary] = useState([]);
  const [loading, setLoading] = useState(false);

  const [filters, setFilters] = useState({
    startDate: "",
    endDate: "",
    minAmount: "",
    maxAmount: "",
  });

  const loadAllData = async (currentFilters) => {
    setLoading(true);
    try {
      const [exp, monthly, category] = await Promise.all([
        fetchExpenses(currentFilters),
        fetchMonthlySummary(currentFilters),
        fetchCategorySummary(currentFilters),
      ]);

      setExpenses(exp);
      setMonthlySummary(monthly);
      setCategorySummary(category);
    } catch (err) {
      console.error("Failed to load data", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    // initial load with no filters
    loadAllData(filters);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const handleUpload = async (file) => {
    const result = await uploadExpenses(file);
    await loadAllData(filters);
    return result;
  };

  const handleApplyFilters = async (newFilters) => {
    setFilters(newFilters);
    await loadAllData(newFilters);
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>Personal Expense Analyzer</h1>
        <p className="muted">
          Upload your expenses CSV, filter by date and amount, and see summaries.
        </p>
      </header>

      <main>
        <UploadForm onUploadSuccess={handleUpload} />

        <FilterBar initialFilters={filters} onApply={handleApplyFilters} />

        {loading && <p className="status">Loading data...</p>}

        <SummaryCards
          expenses={expenses}
          categorySummary={categorySummary}
          filters={filters}
        />

        <section className="grid-2">
          <MonthlyBarChart data={monthlySummary} />
          <CategoryPieChart data={categorySummary} />
        </section>

        <ExpenseTable expenses={expenses} />
      </main>

      <footer className="app-footer">
        <span>Built with Spring Boot + React</span>
      </footer>
    </div>
  );
}

export default App;
