// src/App.js
import React, { useEffect, useState } from "react";
import "./index.css";
import {
  uploadExpenses,
  fetchExpenses,
  fetchMonthlySummary,
  fetchCategorySummary,
} from "./api";
import UploadForm from "./components/UploadForm";
import ExpenseTable from "./components/ExpenseTable";
import MonthlyBarChart from "./components/MonthlyBarChart";
import CategoryPieChart from "./components/CategoryPieChart";

function App() {
  const [expenses, setExpenses] = useState([]);
  const [monthlySummary, setMonthlySummary] = useState([]);
  const [categorySummary, setCategorySummary] = useState([]);
  const [loading, setLoading] = useState(false);

  const loadAllData = async () => {
    setLoading(true);
    try {
      const [exp, monthly, category] = await Promise.all([
        fetchExpenses(),
        fetchMonthlySummary(),
        fetchCategorySummary(),
      ]);

      setExpenses(exp);
      setMonthlySummary(monthly);
      setCategorySummary(category);
    } catch (err) {
      console.error("Failed to load data", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadAllData();
  }, []);

  const handleUpload = async (file) => {
    const result = await uploadExpenses(file);
    // After successful upload, refresh data
    await loadAllData();
    return result;
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>Personal Expense Analyzer</h1>
        <p className="muted">
          Upload your expenses CSV, track spending by month and category.
        </p>
      </header>

      <main>
        <UploadForm onUploadSuccess={handleUpload} />

        {loading && <p className="status">Loading data...</p>}

        <section className="grid-2">
          <MonthlyBarChart data={monthlySummary} />
          <CategoryPieChart data={categorySummary} />
        </section>

        <ExpenseTable expenses={expenses} />
      </main>

      <footer className="app-footer">
        <span>Built with Spring Boot + React + H2</span>
      </footer>
    </div>
  );
}

export default App;
